import { Component } from '@angular/core';
import {
  HttpClient,
  HttpErrorResponse,
  HttpResponse,
  HttpParams,
  HttpHeaders,
} from '@angular/common/http';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css'],
})
export class AppComponent {
  title = 'OTP System';
  email = '';
  OTP = '';
  pageName = 'generateOTP';
  generateOTP = 'https://gdgapiq9ri.execute-api.ap-south-1.amazonaws.com/dev';

  headers = new HttpHeaders()
    .set('content-type', 'application/json')
    .set('Access-Control-Allow-Origin', '*');

  constructor(private http: HttpClient) {}

  getEmail(generateData: { mailID: string }) {
    this.email = generateData.mailID;
    let params = new HttpParams().set('MailID', this.email);
    this.http.get(this.generateOTP, { params }).subscribe((res) => {
      console.log(res),
        (err) => {
          console.log(err);
        };
    });
  }
  getOTP(verifyData: { OTP: string }) {
    this.OTP = verifyData.OTP;
  }
}
<app-home *ngIf="pageName === 'home'"></app-home>
<app-generate-otp
  (getMail)="getEmail($event)"
  *ngIf="pageName === 'generateOTP'"
></app-generate-otp>
<app-verify-otp
  (getOtp)="getOTP($event)"
  *ngIf="pageName === 'verifyOTP'"
></app-verify-otp>
